mapper.select:
SELECT ID id , ORGANIZATION_ID organizationId, TYPE type, NAME name, AGE age, CONTENT content, FEATURE feature, IMG img, CREATE_TIME createTime FROM T_COURSE_INFO WHERE 1=1
AND ORGANIZATION_ID = #{organizationId}
LIMIT #{begin},#{rows}

SELECT 
count(*)
FROM T_COURSE_INFO
WHERE 1=1

	AND ORGANIZATION_ID = #{organizationId}

LIMIT #{begin},#{rows}
